import React, { Component } from "react";
import { Redirect } from "react-router-dom";
import TagList from "./blog-post-preview/TagList";
import axios from "axios";
import { SERVER_URL } from "../../config";
import { post } from "../../../../app/routes/posts";

class Post extends Component {
  constructor(props) {
    super(props);

    this.state = {
      post: [],
      redirect: false,
    };
  }

  render() {
    return (
      <React.Fragment>
        <DoRedirect redirect={this.state.redirect} />
        {post.map((unique) => {
          return (
            <div className="card preview-post mb-3">
              <div className="card-body">
                <p className="card-title primary">{this.state.post.title}</p>
                <p className="card-subtitle text-muted">{`${this.state.post.date} ${this.state.post.time}`}</p>

                <div dangerouslySetInnerHTML={this.state.post.content} />

                <a className="card-link">Read More</a>
                <TagList tags={this.state.post.tags} />
              </div>
            </div>
          );
        })}
      </React.Fragment>
    );
  }

  componentDidMount() {
    axios
      .get(`${SERVER_URL}/posts/${this.props.match.params.id}`)
      .then((post) => {
        if (post) this.setState({ post: post.data[0] });
        else this.setState({ redirect: true });
      });
  }
}

function DoRedirect(props) {
  if (!props.redirect) {
    return null;
  } else {
    return <Redirect to="/invalid" />;
  }
}

export default Post;
