import React, { Component } from "react";
import "./header.css";
import { NavLink, Link } from "react-router-dom";

class Header extends Component {
  constructor(props) {
    super(props);

    this.state = {
      pathname: this.props.pathname,
    };

    this.changePathname = this.changePathname.bind(this);
  }

  changePathname(newPathname) {
    this.setState({ pathname: newPathname });
  }

  render() {
    console.log(this.state.pathname);
    return (
      <header className="main-header pt-5 pb-1">
        <div className="container">
          <nav className="navbar navbar-expand-md justify-content-between">
            <Link className="logo navbar-brand" to="/">
              c<span className="dot">‚óè</span>grass
            </Link>
            <button
              className="navbar-toggler"
              type="button"
              data-toggle="collapse"
              data-target="#navbarNav"
              aria-controls="navbarNavDropdown"
              aria-expanded="false"
              aria-label="Toggle navigation"
            >
              <i className="fa fa-bars custom-toggler" aria-hidden="true"></i>
            </button>
            <div className="collapse navbar-collapse" id="navbarNav">
              <div className="navbar-nav">
                <Link
                  className={`nav-item nav-link ${
                    this.state.pathname === "/about" ? "active" : null
                  }`}
                  to="/"
                >
                  Home
                </Link>
                <Link
                  className={`nav-item nav-link ${
                    this.state.pathname === "/about" ? "active" : null
                  }`}
                  to="/about"
                >
                  About
                </Link>
                <NavLink
                  className="nav-item nav-link"
                  activeClassName="active"
                  to="/contact"
                >
                  Contact
                </NavLink>
                <Link
                  className={`nav-item nav-link ${
                    this.state.pathname === "/portfolio" ? "active" : null
                  }`}
                  to="/portfolio"
                >
                  Portfolio
                </Link>
              </div>
            </div>
          </nav>
        </div>
      </header>
    );
  }
}

export default Header;
