import React from "react";
import { useParams } from "react-router-dom";

const range = 2;

function HomeNav({ posts_per_page, count }) {
  const params = useParams();
  if (!count || !posts_per_page) {
    return null;
  } else {
    const page = params.page ? Number(params.page) : 1;
    let numPages = Math.ceil(count / posts_per_page);
    console.log(page);
    let renderArr = [];
    if (page !== 1) renderArr.push("la");
    renderArr.push(1);
    if (page > 1 + range + 1) renderArr.push("dots");
    for (let i = range; i >= 0; i--) {
      if (page - i > 1 && page - i !== numPages) renderArr.push(page - i);
    }
    for (let i = 1; i <= range; i++) {
      if (page + i < numPages) renderArr.push(page + i);
    }
    if (page + range < numPages - 1) renderArr.push("dots");
    renderArr.push(numPages);
    if (page !== numPages) renderArr.push("ra");
    console.log(renderArr);
    return renderArr.map((symbol, index) => {
      return <HomeNavItem key={index} />;
    });
  }
}

function HomeNavItem({ symbol }) {
  if (symbol === "la") return <div className="home-nav-item ml-1">&lt;</div>;
  else if (symbol === "ra")
    return <div className="home-nav-item ml-1">&rt;</div>;
  else if (symbol === "dots") return <span className="ml-1">...</span>;
  else return <div className="home-nav-item ml-1">{symbol}</div>;
}

export default HomeNav;
